import React, { useEffect, useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import { getTasks } from "./api";

const App = () => {
  const [tasks, setTasks] = useState([]);

  // ⁄Øÿ±ŸÅÿ™ŸÜ ŸÑ€åÿ≥ÿ™ ÿ™ÿ≥⁄©‚ÄåŸáÿß ÿØÿ± ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å ÿßŸàŸÑ€åŸá
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const data = await getTasks();
        setTasks(data);
      } catch (err) {
        console.error("ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ÿ™ÿ≥⁄©‚ÄåŸáÿß:", err);
      }
    };
    fetchTasks();
  }, []);

  // ÿßŸÅÿ≤ŸàÿØŸÜ ÿ™ÿ≥⁄© ÿ¨ÿØ€åÿØ
  const handleTaskAdded = (newTask) => {
    setTasks([newTask, ...tasks]);
  };

  // ÿ≠ÿ∞ŸÅ ÿ™ÿ≥⁄©
  const handleTaskDeleted = (id) => {
    setTasks(tasks.filter((task) => task._id !== id));
  };

  // ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ™ÿ≥⁄©
  const handleTaskUpdated = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task._id === updatedTask._id ? updatedTask : task))
    );
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ŸÖÿØ€åÿ±€åÿ™ ÿ™ÿ≥⁄©‚ÄåŸáÿß üìù</h2>
      <TaskForm onTaskAdded={handleTaskAdded} />
      <TaskList
        tasks={tasks}
        onTaskDeleted={handleTaskDeleted}
        onTaskUpdated={handleTaskUpdated}
      />
    </div>
  );
};

export default App;
